import { useState } from "react";

const FORM_ENDPOINT = "http://127.0.0.1:5011/";

const Form = () => {
    const [picture, setPicture] = useState({});
    const [prediction, setPrediction] = useState({});

    const uploadPicture = (e) => {
      setPicture({
        /* contains the preview, if you want to show the picture to the user
             you can access it with this.state.currentPicture
         */
        picturePreview: URL.createObjectURL(e.target.files[0]),
        /* this contains the file we want to send */
        pictureAsFile: e.target.files[0],
      });
    };

    const setImageAction = async (event) => {
      event.preventDefault();

      const data = new FormData(event.target);

      fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      })
      .then((res) => res.json())
      .then((medicalPrescription) => {
        console.log(medicalPrescription)
        setPrediction(medicalPrescription);
      });

      console.log("Successfully uploaded image: ", prediction);

      /*
      const res = await fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      });
      const result = await res.json();
      setPrediction(result);

      if (result) {
        console.log("Successfully uploaded image: ", result);
        console.log("Successfully uploaded image: ", prediction);
      } else {
        console.log("Error Found");
      }
      */
      /*
      fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((prediction) => {
          setPrediction(prediction);
      });

      console.log(prediction)
      */
    };

    return (
      <div className="">
        <form onSubmit={setImageAction} encType="multipart/form-data">
          <input type="file" name="file" onChange={uploadPicture} />
          <br />
          <br />
          <button type="submit" name="upload" className="bg-white text-black rounded-lg shadow-lg h-10 rounded-lg tracking-wide uppercase border border-blue cursor-pointer hover:bg-black hover:text-white transform cursor:pointer hover:scale-105 cursor-pointer transition duration-500 linear w-60">
            Predikovať
          </button>
        </form>
        <div id="result">
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">Druh huby:</h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.label}</h1>
          </div>
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">Jedlosť huby:</h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.edibility}</h1>
          </div>
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">
              Presnosť predikcie:
            </h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.percentage}%</h1>
          </div>
        </div>
      </div>
    );
  };

export default Form;
