import { useState } from "react";

function useForm({ form, endpointUrl }) {
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");
  const [label, setLabel] = useState("");
  const [edibility, setEdibility] = useState("");
  const [percentage, setPercentage] = useState("");

  const handleSubmit = (e) => {
    if (form) {
      e.preventDefault();
      setStatus("loading");
      setMessage("");

      console.log(form.elements)
      const finalFormEndpoint = endpointUrl || form.action;
      const data = Array.from(form.elements)
        .filter((input) => input.name)
        .reduce(
          (obj, input) => Object.assign(obj, { [input.name]: input.value }),
          {}
        );

      fetch(finalFormEndpoint, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      })
        .then((res) => res.json())
        .then((data) => {
            console.log(data)
          setMessage("We'll be in touch soon.");
          setStatus("success");
          setLabel(data.label);
          setEdibility(data.edibility);
          setPercentage(data.percentage);
        })
        .catch((err) => {
          setMessage(err.toString());
          setStatus("error");
        });
    }
  };

  return { handleSubmit, status, message, label, edibility, percentage };
}

export default useForm;
