import { useState } from "react";

const FORM_ENDPOINT = "http://127.0.0.1:5011/";

const Form = () => {
    const [picture, setPicture] = useState({});
    const [prediction, setPrediction] = useState({});

    const uploadPicture = (e) => {
      setPicture({
        /* contains the preview, if you want to show the picture to the user
             you can access it with this.state.currentPicture
         */
        picturePreview: URL.createObjectURL(e.target.files[0]),
        /* this contains the file we want to send */
        pictureAsFile: e.target.files[0],
      });
    };

    const setImageAction = async (event) => {
      event.preventDefault();

      const data = new FormData(event.target);

      fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      })
      .then((res) => res.json())
      .then((medicalPrescription) => {
        console.log(medicalPrescription)
        setPrediction(medicalPrescription);
      });

      console.log("Successfully uploaded image: ", prediction);

      /*
      const res = await fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      });
      const result = await res.json();
      setPrediction(result);

      if (result) {
        console.log("Successfully uploaded image: ", result);
        console.log("Successfully uploaded image: ", prediction);
      } else {
        console.log("Error Found");
      }
      */
      /*
      fetch(FORM_ENDPOINT, {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((prediction) => {
          setPrediction(prediction);
      });

      console.log(prediction)
      */
    };

    return (
      <div className="">
        <form onSubmit={setImageAction} encType="multipart/form-data">
          <input type="file" name="file" onChange={uploadPicture} />
          <div class="flex flex-col">
          <div class="flex w-full items-center justify-center bg-grey-lighter">
            <label
              class="w-60 flex flex-col items-center px-4 py-4 bg-white text-black rounded-lg shadow-lg tracking-wide uppercase border border-blue cursor-pointer hover:bg-black hover:text-white transform cursor:pointer hover:scale-105 cursor-pointer transition duration-500 linear"
            >
              <svg
                class="w-8 h-8"
                fill="currentColor"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
              >
                <path
                  d="M16.88 9.1A4 4 0 0 1 16 17H5a5 5 0 0 1-1-9.9V7a3 3 0 0 1 4.52-2.59A4.98 4.98 0 0 1 17 8c0 .38-.04.74-.12 1.1zM11 11h3l-4-4-4 4h3v3h2v-3z"
                />
              </svg>
              <span
                class="mt-2 text-base leading-normal drop-shadow-xl text-center"
                >Vyberte obr치zok huby</span
              >
              <input
                type="file"
                class="hidden"
                name="file"
                id="imgInp"
                accept=".png, .jpg, .jpeg"
                onchange="loadFile(event)"
              />
            </label>
          </div>
        </div>
          <br />
          <br />
          <button type="submit" name="upload" className="hidden bg-white text-black rounded-lg shadow-lg h-10 rounded-lg tracking-wide uppercase border border-blue cursor-pointer hover:bg-black hover:text-white transform cursor:pointer hover:scale-105 cursor-pointer transition duration-500 linear w-60">
            Predikova콘
          </button>
        </form>
        <div id="result">
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">Druh huby:</h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.label}</h1>
          </div>
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">Jedlos콘 huby:</h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.edibility}</h1>
          </div>
          <div className="flex flex-row">
            <h1 className="text-lg font-semibold text-center">
              Presnos콘 predikcie:
            </h1>
            <h1 className="text-lg text-center pl-2">{prediction.predictions?.percentage}%</h1>
          </div>
        </div>
      </div>
    );
  };

export default Form;
